INSERT INTO xcart_languages VALUES ('en','txt_gcheckout_order_list_status_note','<b>Note:</b> Statuses of the orders placed using Google Checkout cannot be changed here. To update them, use the \'Google Checkout: Order processing\' section of the order\'s details page or Google Checkout Merchant Center.','Text');
INSERT INTO xcart_languages VALUES ('en','txt_gcheckout_order_status_note','<b>Note:</b> This order has been created using Google Checkout. Please, use Google Checkout commands to update the order status.','Text');
