INSERT INTO xcart_languages VALUES ('en','txt_shipping_cost_convertion_rate_au_dollars','The shipping cost is always returned in Australian Dollars. So you need to specify the conversion rate to convert the shipping cost returned by shipping service into the necessary currency.','Text');
