INSERT INTO xcart_languages VALUES ('en','txt_shipping_labels_help','To obtain a USPS shipping label for an order, download the label image generated by USPS for that order and print it out. To obtain a UPS shipping label, download the CSV file generated by X-Cart, import it into UPS WorldShip, allow UPS WorldShip to generate a shipping label, then print out the resulting label image.','Text');
